import React from 'react';
import { Link } from "react-router-dom";
import "./Home.css";
import{Logo} from "../images/Netflix";
import {ConnectButton,Icon,Button,TabList,Tab} from 'web3uikit';
import{movies} from "../helpers/library";
import{useState} from "react";
const Home = () => {
  const[visible,setVisible]=useState(false);
  const [selectedFilm,setSelectedFilm]=useState();

return(
  <>
  <div className="logo"/>
  <Logo/>
  <div className="connect">
    <Icon fill="#ffffff" size={24}svg="bell"/>
    <ConnectButton/>

  </div>

  <div className="topBanner ">
  <TabList
    defaultActiveKey={1}
    tabStyle="bar">
    <Tab tabKey={1} tabName={"Movies"}>
    <div className="scene">
       <img src={movies[0].Scene} 
    className="sceneImg"></img>
       <img className="sceneLogo" src={movies[0].Logo}></img>
      <p className="sceneDesc">{movies[0].Description}</p>
      <div className="playButton">
        <Button
        icon="chevronRightX2"
        text="Play"
        theme="secondary"
        type="button"
        />
           <Button
        icon="plus"
        text="Add to My List"
        theme="translucent"
        type="button"
        />
      </div>
      </div>
      <div className="title">
        Movies
      </div>
      <div className="thumbs">
        {movies && movies.map((e)=>{
          return(
            <img src={e.Thumnbnail}
            className="thumbnail"
              onClick={()=>{
                setSelectedFilm(e);
                setVisible(true);
  (true);
              }}>
            </img>
          )
         })
         }

      </div>
    </Tab>
    <Tab tabKey={2} tabName={"Series"}> </Tab>
    <Tab tabKey={3 } tabName={"MyList"}></Tab>
    </TabList>
  </div>
  </>
)
}

export default Home;
